mapper.insert:

            SELECT 'APP_HSTR-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_APP_HSTR_PK.NEXTVAL, 6, '0') FROM DUAL

	        INSERT INTO APP_HSTR (
		           APP_HSTR_ID
		         , APP_HSTR_RGST_DT
		         , CRS_INF_ID
		         , USR_ID
		    ) SELECT #{appHstrId}
		           , SYSDATE
		           , #{crsInfId}
		           , u.USR_ID
		    FROM USR u
		    WHERE u.USR_ML = #{usrMl}

mapper.select:
        SELECT MAX(ah.APP_HSTR_ID)
	      FROM APP_HSTR ah
	     INNER JOIN USR u 
	        ON ah.USR_ID = u.USR_ID
	     WHERE ah.CRS_INF_ID = #{crsInfId}
	       AND u.USR_ML = #{usrMl}
