mapper.delete:
		UPDATE FL_ARCH
           SET FL_ARCH_DEL_DT = SYSDATE
             , FL_ARCH_DEL_YN = 'Y'
         WHERE FL_ARCH_ID = #{flArchId}
           AND LGN_ID = #{lgnId}

mapper.update:
		UPDATE FL_ARCH
           SET FL_ARCH_TTL = #{flArchTtl}
             , FL_ARCH_CTT = #{flArchCtt}
             , FL_ARCH_UPDT_DT = SYSDATE
         WHERE FL_ARCH_ID = #{flArchId}
           AND LGN_ID = #{lgnId}
           AND FL_ARCH_DEL_YN = 'N'
